import "core-js/modules/es.array.push.js";
export default {
  data() {
    return {
      // список слогов
      syllables: ['по', 'ей', 'он', 'вы', 'мы', 'уж', 'мо', 'во', 'ра', 'ай', 'оп', 'ну', 'пе', 'ой', 'то', 'пу', 'оз', 'ур', 'со', 'рё', 'ни', 'фё', 'ог', 'цо', 'ут', 'гу', 'вю', 'ки', 'бо', 'фя', 'хе', 'яс', 'ру', 'бэ', 'до', 'зя', 'хи', 'уф', 'ир', 'ве', 'ля', 'ша', 'ат', 'ми', 'сё', 'пэ', 'ны', 'ма', 'зу', 'кю', 'жи'],
      // список стихов
      poems: [{
        author: 'Иван Бунин',
        poem: `*ка+тит+ся +дис+ком +зо+ло+тым\n*лу+на +в +про+ва+лы +чёр+ной +ту+чи,\n*и +та+ет +в +ней, +и +льёт +сквозь +дым\n*свой +блеск +на +ка+мен+ны+е +кру+чи.`
      }, {
        author: 'Иван Бунин',
        poem: `*да+ле+ко +на +се+ве+ре +Ка+пел+ла\n*бле+щет +се+миц+вет+ным +о+гонь+ком,\n*и +от+ту+да, +с +по+ля, +тя+нет +ров+ным,\n*лас+ко+вым +по+лу+ноч+ным +теп+лом.`
      }, {
        author: 'Сергей Есенин',
        poem: `*ни+вы +сжа+ты, +ро+щи +го+лы,\n*от +во+ды +ту+ман +и +сы+рость.\n*ко+ле+сом +за +си+ни +го+ры\n*сол+нце +ти+хо+е +ска+ти+лось.`
      }, {
        author: 'Сергей Есенин',
        poem: `*дрем+лет +взры+тая +до+ро+га.\n*ей +се+год+ня +при+меч+та+лось,\n*что +сов+сем-+сов+сем +нем+но+го\n*ждать +зи+мы +се+дой +ос+та+лось.`
      }, {
        author: 'Афанасий Фет',
        poem: `*о+пять +осен+ний +блеск +ден+ни+цы\n*дро+жит +об+ман+чи+вым +ог+нём,\n*и +у+го+вор +за+во+дят +пти+цы\n*ум+чать+ся +ста+ей +за +теп+лом.`
      }, {
        author: 'Федор Тютчев',
        poem: `*есть +в +о+се+ни +пер+во+на+чаль+ной\n*ко+рот+кая, +но +див+на+я +по+ра —\n*весь +день +сто+ит +как +бы +хрус+таль+ный,\n*и +лу+че+зар+ны +ве+че+ра.`
      }, {
        author: 'Федор Тютчев',
        poem: `*где +бод+рый +серп +гу+лял +и +па+дал +ко+лос,\n*те+перь +уж +пус+то +всё — +прос+тор вез+де,\n*лишь +па+у+ти+ны +тон+кий +во+лос\n*блес+тит +на +праз+дной +бо+роз+де.`
      }, {
        author: 'Александр Блок',
        poem: `*е+ще +свет+ло +пе+ред +ок+ном,\n*в +раз+ры+вы +об+лак +сол+нце +бле+щет,\n*и +во+ро+бей +сво+им +кры+лом,\n*в +пес+ке +ку+па+я+ся, +тре+пе+щет.`
      }, {
        author: 'Афанасий Фет',
        poem: `*а +уж +от +не+ба +до +зем+ли,\n*ка+ча+ясь, +дви+жет+ся +за+ве+са,\n*и +буд+то +в +зо+ло+той +пы+ли\n*сто+ит +за +ней +о+пуш+ка +ле+са.`
      }],
      authorElement: null,
      poemElement: null,
      poemContainer: null,
      color: null,
      fontSize: null,
      // сложность 1,2,3,4 это сколько строк нужно выводить
      level: null,
      syllable: null
    };
  },
  mounted() {
    this.authorElement = document.getElementById('author');
    this.poemElement = document.getElementById('poem');
    this.poemContainer = document.getElementById('poem-container');
    this.generatePoem();
  },
  methods: {
    generatePoem() {
      // берём данные с формы
      this.color = document.getElementById('color').value;
      this.fontSize = +document.getElementById('fontSize').value;
      this.level = +document.getElementById('level').value;
      this.syllable = document.getElementById('syllable').value;
      this.poemContainer.style.cssText = `
                color: ${this.color};
                font-size: ${this.fontSize}px;
            `;
      // выбираем рандомно ондин из стихов
      let poem = this.poems[this.getRandomInt(0, this.poems.length - 1)];
      // Выводим автора стиха
      this.authorElement.innerText = poem.author;
      // Добавляем слог
      this.poemElement.innerText = this.addSyllables(this.syllable, poem.poem, this.level);
    },
    addSyllables(syllable, poem, level) {
      // разделяем каждую строку
      let rows = poem.split('\n');
      // массив для результата
      let result = [];
      // перебераем нужные нам строчки
      for (let rowNumber = 0; rowNumber < level; rowNumber++) {
        const row = rows[rowNumber];
        // берём в саммив каждое слово в строчке
        let words = row.split(' ');
        // переберяем каждое слово
        let addedSyllable = words.map(word => {
          // удаляем знак * и вставляем слог с большой буквой в начале
          let forReturn = word.replace('*', syllable.split('')[0].toUpperCase() + syllable.slice(1));
          // удаляем знак + и вставляем слог
          forReturn = forReturn.replaceAll('+', syllable);
          // возвращаем слово
          return forReturn;
        });
        // соединяем каждое слово в строчку
        result.push(addedSyllable.join(' '));
      }
      // в конце соединяем все строчки. Каждая строчка по новой линии
      return result.join('\n');
    },
    getRandomInt(min, max) {
      min = Math.ceil(min);
      max = Math.floor(max);
      return Math.floor(Math.random() * (max - min + 1)) + min;
    }
  }
};
